import { Link } from 'react-router-dom'
import axios from 'axios'
import * as Apis from '../../context/Api'
import React, { useState, useEffect } from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Pagination from '@mui/material/Pagination';
import Spinner from '../../Components/spinner/Spinner';
// delete mui button
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import Button from '@mui/material/Button';
import Info from '../../context/Info';
// delete mui button close

export default function Techknowlogylist() {
    const [spinner, setspinner] = useState(true)
    const [totalCount, settotalCount] = useState(0)
    const [tect, settect] = useState([]);
    const [sortOrder, setsortOrder] = useState("asc")
    const [deletId, setdeletId] = useState('')
    const [open, setOpen] = useState(false);
    const [pageNbr, setpageNbr]=useState()
    // 
    const [delStatus, setdelStatus] = useState('false')
    const [status, setstatus] = useState("true")
    const [searchTerm, setsearchTerm] = useState("")

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    let key = JSON.parse(localStorage.getItem('userinfo'))._id
    let Count = 5

    const sorting = (col) => {
        if (sortOrder === "asc") {
            const sorted = [...tect].sort((a, b) =>
                a[col].toLowerCase() > b[col].toLowerCase() ? 1 : -1
            );
            settect(sorted);
            setsortOrder("dsc")
        }
        if (sortOrder === "dsc") {
            const sorted = [...tect].sort((a, b) =>
                a[col].toLowerCase() < b[col].toLowerCase() ? 1 : -1
            );
            settect(sorted);
            setsortOrder("asc")
        }
    }

    useEffect(
        () => {

            getData(1)

        }, []
    )

    useEffect(
        () => {
            if (status === 'true') {


                getData(1)
            }

        }, [status]
    )
    useEffect(
        () => {
            if (delStatus === 'false') {


                getData(1)
            }

        }, [delStatus]
    )
    useEffect(
        () => {
            if (searchTerm === '') {
                getData(1)
            }

        }, [searchTerm]
    )


    const getData = (page) => {
        let data = {

            userId: key,
            count: Count,
            page: page,
            searchText: searchTerm,
            isActive: status,
            isDeleted: delStatus,
        }

        axios.post(Apis.tecList(), data).then((response) => {

            settect(response.data.data)
            settotalCount(response.data.totalCount)
            setspinner(false)
            setpageNbr(page)

        })
    }; 
    const deleteTitle = (_id) => {
        let tecdel ={
            _id :_id,
            userId:Info.userInfo._id
        }
            axios.post(Apis.tecDelete(),tecdel,{headers : {'x-access-token':Info.token}}).then((response) => {
                toast(response.data.message);
                getData()
                setOpen(false);
            })

        }

    return (
        <div>
            {
                spinner && <Spinner />
            }
            <div className="container">
                <div>
                    <h3 className="text-center">   Technology list</h3>
                    <div className="text-left d-flex justify-content-end">
                        <Link className="btn btn-warning " to="/Techknowlogyadd">
                            <h4> Add </h4>
                        </Link>
                    </div>
                </div>

                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col" >S.no</th>
                            <th scope="col" onClick={() => sorting("title")}>Title<i className="bi bi-chevron-down"></i></th>
                            <th scope="col" onClick={() => sorting("description")}>Description<i className="bi bi-chevron-down"></i></th>
                            <th scope="col" >Actions</th>

                        </tr>
                    </thead>
                    <tbody>
                        {
                            tect.map((item, index) => (

                                <tr key={item._id}>
                                    <th scope="row">{Count * (pageNbr - 1) + index + 1}</th>
                                    <td className="col-2">{item.title} </td>
                                    <td className="col-6">{item.description} </td>

                                     <td >
                                        <Link className="btn btn-success m-2" to={`/TechknowlogyEdit/${item._id}`}  ><i className="bi bi-pencil-square"></i></Link>

                                        <button className="btn btn-danger" onClick={() => {
                                            setdeletId(item._id)
                                            handleClickOpen()
                                        }}>
                                            <i className="bi bi-trash"></i>
                                        </button>
                                    <Dialog
                                            open={open}
                                            onClose={handleClose}
                                            aria-labelledby="draggable-dialog-title"
                                        >

                                            <DialogContent>
                                                <DialogContentText>
                                                    Are you sure to delete this information..?
                                                </DialogContentText>
                                            </DialogContent>
                                            <DialogActions>
                                                <Button autoFocus onClick={handleClose}>
                                                    No
                                                </Button>
                                                 <Button onClick={() => { deleteTitle(deletId) }}>Yes </Button> 

                                            </DialogActions>
                                        </Dialog>
                                    </td> 
                                </tr>
                            )
                            )}

                    </tbody>
                </table>
           <div> 
               { totalCount > Count ? (
                <div className="d-flex justify-content-center m-4">               

                    <Pagination
                        count={Math.ceil(totalCount / Count)}
                        showFirstButton
                        showLastButton
                        onChange={(event, value) => {
                            getData(value)
                        }}
                    /> 
                </div>
                       ): null}
                 </div>
            </div>
            <ToastContainer />
        </div>
    )
}
