import { Button, FormControl, FormGroup, Input, InputLabel, makeStyles, Typography } from '@material-ui/core'
import React from 'react'

import { useFormik } from 'formik'
import { TextField } from '@material-ui/core'
import * as yup from 'yup'
import { Link, useHistory } from 'react-router-dom'
import axios from 'axios'
import * as Apis from '../../enviornment/Apis'



export default function Adddesignation() {


    const useStyle = makeStyles(
        {
            container: {
                width: '50%',
                margin: '5% 0 0 25%',
                '& > *': {
                    marginTop: 20
                }
            }
        }
    )

    const formik = useFormik({
        initialValues: {
            title: "",
            createdById: "61a0dab0777b848f7b22f811",


        },

        validationSchema: yup.object({
            title: yup.string().required("Title is required")
        }),

        onSubmit: (values) => {
            console.log(values);
            submitData(values)
        }
    });

    let submitData = (values) => {

        axios.post(Apis.add(), values).then((response) => {
            if (response.data.code === 200) {


                history.push('/designationlist')
            }
        })

    }



    const history = useHistory();
    const classes = useStyle();

    return (
        <div className={classes.container}>
            <h1>Designation</h1>
            <form onSubmit={formik.handleSubmit}>
                <div className="position-relative">
                    <TextField
                        label="Enter Designation"
                        name="title"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.title}
                        type='text'
                    /><br></br>
                    {formik.touched.title && formik.errors.title ? (
                        <span className="text_error_message text-danger"> {formik.errors.title} </span>
                    ) : null}
                </div>
                <div className="dropdown">
                    <button className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                        Dropdown button
                    </button>
                    <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                        <Link className="dropdown-item" to="#">Action</Link>
                        <Link className="dropdown-item" to="#">Another action</Link>
                        <Link className="dropdown-item" to="#">Something else here</Link>
                    </div>
                </div>
                <br></br>
                <div >
                    <button type="submit" className="btn btn-danger">Add Designation</button>

                </div>

            </form>
        </div>
    )
}
