import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow } from '@mui/material'
import Paper from '@mui/material/Paper';
import axios from 'axios';
import React, {useState, useEffect} from 'react'
import Info from '../../context/Info';
import * as Apis from '../../context/Api'
import { Link } from 'react-router-dom';
import Pagination from '@mui/material/Pagination';


export default function SkillsList() {

    const [skills, setskills] = useState([])

   useEffect(() => {
       getData()
       }
   , [])


    const getData = ()=>{
        let Data = {
            createdById: Info.userInfo._id
        };

        axios.post(Apis.skillsList(),Data).then((response)=>{
            setskills(response.data.data);
            console.log(response.data.data);
        })
    }


    return (
        <div className='container'>
            <div>
                    <div className="text-left d-flex justify-content-end">
                        <Link className="btn btn-warning " to="/addSkilss">
                            <h4>Add Skills</h4>
                        </Link>
                    </div>
            </div>
            <TableContainer component={Paper}>
                <Table area-aria-label='simple table'>
                    <TableHead>
                        <TableRow>
                            <TableCell>
                                S.no
                            </TableCell>
                            <TableCell>
                                Skills
                            </TableCell>
                            <TableCell>
                                Action
                            </TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {
                            skills.map((item,index)=>(
                                <TableRow key={index}>
                                    <TableCell>
                                        {index+1}
                                    </TableCell>
                                    <TableCell>
                                        {item.title}
                                    </TableCell>
                                </TableRow>
                            ))
                        }
                    </TableBody>
                </Table>
                <div className=" d-flex justify-content-center m-4">
                    <Pagination/>
                </div>
            </TableContainer>
        </div>
    )
}
