import { Link } from 'react-router-dom'
import axios from 'axios'
import * as Apis from '../../context/Api'
import React, { useState, useEffect } from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Pagination from '@mui/material/Pagination';
import Spinner from '../../Components/spinner/Spinner';
// delete mui button
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import Button from '@mui/material/Button';
import Info from '../../context/Info';
import Switch from '@mui/material/Switch';

// delete mui button close

export default function Statelist() {
    const [spinner, setspinner] = useState(true)
    const [totalCount, settotalCount] = useState(0)
    const [stateList, setstateList] = useState([]);
    const [sortOrder, setsortOrder] = useState("asc")
    const [deletId, setdeletId] = useState('')
    const [open, setOpen] = useState(false);
    const [pageNbr, setpageNbr] = useState()
    // 
    const [delStatus, setdelStatus] = useState('false')
    const [status, setstatus] = useState("true")
    const [searchTerm, setsearchTerm] = useState("")
    const [toggle, settoggle] = useState(true)
    const [checked, setChecked] = React.useState(true);




    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };


    let Count = 5

    const sorting = (col) => {
        if (sortOrder === "asc") {
            const sorted = [...stateList].sort((a, b) =>
                a[col].toLowerCase() > b[col].toLowerCase() ? 1 : -1
            );
            setstateList(sorted);
            setsortOrder("dsc")
        }
        if (sortOrder === "dsc") {
            const sorted = [...stateList].sort((a, b) =>
                a[col].toLowerCase() < b[col].toLowerCase() ? 1 : -1
            );
            setstateList(sorted);
            setsortOrder("asc")
        }
    }

    useEffect(
        () => {

            getData(1)

        }, []
    )

    useEffect(
        () => {
            if (status === 'true') {


                getData(1)
            }

        }, [status]
    )
    useEffect(
        () => {
            if (delStatus === 'false') {


                getData(1)
            }

        }, [delStatus]
    )
    useEffect(
        () => {
            if (searchTerm === '') {
                getData(1)
            }

        }, [searchTerm]
    )

    const getData = (page) => {
        let data = {
            createdById: Info.userInfo._id,
            page: page,
            count: Count,
            searchText: searchTerm,
            isActive: status,
            isDeleted: delStatus,
        }

        axios.post(Apis.stateList(), data).then((response) => {
            setstateList(response.data.data)
            settotalCount(response.data.totalCount)
            setspinner(false)
            setpageNbr(page)


        })

    };
    const handleChange = (event) => {
        setChecked(event.target.checked);
    };
    const activeStatus = (_id) => {
        let activeData = {
            _id: _id,
            userId: Info.userInfo._id,
            isActive: toggle

        }
        axios.post(Apis.StateToggle(), activeData, { headers: { 'x-access-token': Info.token } }).then((response) => {
            getData(1);
            toast(response.data.message);
            setChecked(event.target.checked);

        })
    }


    const deleteTitle = (_id) => {
        let statedel = {
            _id: _id,
            userId: Info.userInfo._id
        }
        axios.post(Apis.StateDelete(), statedel, { headers: { 'x-access-token': Info.token } }).then((response) => {
            toast(response.data.message);
            getData(1)
            setOpen(false);
        })

    }
    return (
        <div>
            {
                spinner && <Spinner />
            }
            <div className="container">
                <div className='row'>
                    <h1 className="text-center m-4">State list</h1>
                    <div className="col-3   ">
                        <input type="text" placeholder="Search..." value={searchTerm} className="form-control mb-2"
                            onChange={(e) => {
                                setsearchTerm(e.target.value)
                            }}
                        ></input>
                    </div>
                    <div className="col-3">

                        <select onChange={(e) => {
                            console.log(e.target.value)
                            setstatus(e.target.value)
                        }} class="form-select" aria-label="Default select example">
                            {/* <option selected>Active status</option> */}

                            {<option value="true"

                            >Active</option>}
                            <option value="false">
                                In active</option>
                        </select>
                    </div>
                    <div className="col-3">
                        <select onChange={(e) => {
                            setdelStatus(e.target.value)
                        }} class="form-select" aria-label="Default select example">
                            <option selected>Delete Status</option>

                            <option value="true">Deleted</option>
                            <option value="false">Not Deleted</option>
                        </select>
                    </div>
                    <div className='col-1'>
                        <span>
                            <button type="submit" className="btn btn-primary " onClick={() => {
                                if (searchTerm || status || delStatus) {
                                    // console.log(status);
                                    getData(1)
                                }

                            }} >Search</button></span>
                    </div>
                    <div className='col-1'>
                        <span>
                            <button className="btn btn-danger " onClick={() => {
                                setsearchTerm('')
                                setdelStatus('false')
                                setstatus('true')

                            }}> clear</button>
                        </span>
                    </div>
                    <div className="col-1">

                        <div className="text-left">
                            <Link className="btn btn-warning " to="/Addstate">
                                Add
                            </Link>
                        </div>
                    </div>
                </div>
                {stateList.length > 0 ? (

                    <table className="table">
                        <thead>
                            <tr>
                                <th scope="col" >S.no</th>
                                <th scope="col" onClick={() => sorting("title")}>State<i className="bi bi-chevron-down"></i></th>
                                <th scope="col" onClick={() => sorting("title")}>Country<i className="bi bi-chevron-down"></i></th>
                                <th scope="col" >Actions</th>
                                <th scope="col" >Status</th>

                            </tr>
                        </thead>
                        <tbody>
                            {
                                stateList.map((item, index) => (

                                    <tr key={item._id}>
                                        <th scope="row">{Count * (pageNbr - 1) + index + 1}</th>
                                        <td className="col-2">{item.title} </td>
                                        <td className="col-2">{item.countryTitle} </td>


                                        <td >
                                            <Link className="btn btn-success m-2" to={`/StateEdit/${item._id}`}  ><i className="bi bi-pencil-square"></i></Link>

                                            <button className="btn btn-danger" onClick={() => {
                                                setdeletId(item._id)
                                                handleClickOpen()
                                            }}>
                                                <i className="bi bi-trash"></i>
                                            </button>
                                            <Dialog
                                                open={open}
                                                onClose={handleClose}
                                                aria-labelledby="draggable-dialog-title"
                                            >

                                                <DialogContent>
                                                    <DialogContentText>
                                                        Are you sure to delete this information..?
                                                    </DialogContentText>
                                                </DialogContent>
                                                <DialogActions>
                                                    <Button autoFocus onClick={handleClose}>
                                                        No
                                                    </Button>
                                                    <Button onClick={() => { deleteTitle(deletId) }}>Yes </Button>

                                                </DialogActions>
                                            </Dialog>
                                        </td>
                                        <td>
                                            {/* <form>
                                        <div class="form-check form-switch">
                                            <input class="form-check-input" type="checkbox" id="flexSwitchCheckDefault" checked 
                                            onClick={()=>{
                                                activeStatus(item._id)
                                                if(toggle===false){
                                                settoggle(true)
                                                }
                                                else if(toggle===true){
                                                    settoggle(false)
                                                }


                                            }}
                                            ></input>

                                        </div>
                                        </form> */}

                                            <Switch
                                                checked={checked}
                                                onChange={handleChange}
                                                inputProps={item._id }
                                                onClick={()=>{
                                                    activeStatus(item._id)
                                                    if(toggle===false){
                                                    settoggle(true)
                                                    }
                                                    else if(toggle===true){
                                                        settoggle(false)
                                                    }


                                                }}
                                            />
                                        </td>
                                    </tr>
                                )
                                )}

                        </tbody>
                    </table>
                ) : <div className='text-center mt-5'>No record found</div>}

                <div>
                    {totalCount > Count ? (
                        <div className="d-flex justify-content-center m-4">

                            <Pagination
                                count={Math.ceil(totalCount / Count)}
                                showFirstButton
                                showLastButton
                                onChange={(event, value) => {
                                    getData(value)
                                }}
                            />
                        </div>
                    ) : null}
                </div>
            </div>
            <ToastContainer />
        </div>
    )
}
