import { Button, FormControl, FormGroup, Input, InputLabel, makeStyles, Typography } from '@material-ui/core'
import React from 'react'
import { useState } from 'react'
import { addUser } from '../../enviornment/Apis'
import { useHistory } from 'react-router'
import validation from '../../enviornment/validation'
import { useFormik } from 'formik'
import { TextField } from '@material-ui/core' 
import * as yup from 'yup'
import { Link } from 'react-router-dom'





export default function Adduser() {
    const useStyle= makeStyles(
        {
            container:{
                width:'50%',
                margin:'5% 0 0 25%',
                '& > *':{
                    marginTop: 20
                }
            }
        }
    )

    const formik = useFormik({
        initialValues: {
            title: ""
        },

        validationSchema: yup.object({
            title: yup.string().required("Title is required")
        }),

        onSubmit: (values)=>{
            console.log(values);
        }
    })
    const classes= useStyle();

    return (
        <div className={classes.container}>
            <form onSubmit={formik.handleSubmit}>
                <div className="position-relative">
                    <TextField 
                        label="Enter Designation"
                        name="title"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.title}
                        type='text'
                    />
                </div>
                { formik.touched.title && formik.errors.title ? (
                    <span className="text_error_message text-danger"> {formik.errors.title} </span> 
                ):null }

                <div className="text-center">
                    <button type="submit" className="btn btn-danger">Add Designation</button>
                    <Link to="/userlist" className="btn btn-success m-3">Login</Link>
                </div>

            </form>
        </div>
    )
}
