import { Button, makeStyles, Table, TableBody, TableCell, TableHead, TableRow } from '@material-ui/core';
import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios'
import * as Apis from '../../enviornment/Apis'
import './designation.css';
import TablePagination from '@mui/material/TablePagination';
import TableContainer from '@mui/material/TableContainer';
import Paper from '@mui/material/Paper';



const useStyle = makeStyles({
    table: {
        width: '70%',
        margin: '10px 0 0 100px',
        fontSize: '50'
    },
    thead: {
        '& > *': {
            background: '#000000',
            color: '#ffffff'
        }
    }
})
export default function Designationlist() {

    const classes = useStyle();
    const [users, setUsers] = useState([]);
    //Pagination starts

    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);
    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = event => {
        setRowsPerPage(parseInt(event.target.value, 10));
        setPage(0);
    };
    const emptyRows =
        rowsPerPage - Math.min(rowsPerPage, rows.length - page * rowsPerPage);

    // pagination ends
    useEffect(
        () => {
            getData()

        }, []
    )
    const getData = () => {
        let data = {
            createdById: "61a0dab0777b848f7b22f811",
        }
        axios.post(Apis.deslist(), data).then((response) => {
            setUsers(response.data.data)
        })

    };

    const deleteUserData = (_id) => {
        axios.post(Apis.delDes(), {
            _id: _id
        }).then((response) => {
            getData()
            toast(response.data.message);
        })

    }

    return (
        <div>
            <Link to="/adddesignation" className="btn btn-primary marginleft">Add New Designation</Link>
            <TableContainer component={Paper}>
                <Table sx={{ minWidth: 650 }} aria-label="simple table">
                    <TableHead>
                        <TableRow >

                            <TableCell>S.No</TableCell>

                            <TableCell>Designation</TableCell>

                            <TableCell>Action</TableCell>

                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {rows
                            .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                            .users.map((user, index) => (
                                <TableRow key={index}>

                                    <TableCell>
                                        {index + 1}
                                    </TableCell>


                                    <TableCell>
                                        {user.title}
                                    </TableCell>

                                    <TableCell>
                                        <Button variant="contained" color="primary" style={{ marginRight: 10 }} component={Link} to={`/editdesignation/${user._id}`}>Edit</Button>
                                        <Button variant="contained" color="secondary" onClick={() => { deleteUserData(user._id) }} >Delete</Button>
                                    </TableCell>
                                </TableRow>
                            ))
                        }
                        {emptyRows > 0 && (
                            <TableRow style={{ height: 53 * emptyRows }}>
                                <TableCell colSpan={6} />
                            </TableRow>
                        )}
                    </TableBody>

                </Table>
                <TablePagination
                    rowsPerPageOptions={[5, 10, 25]}
                    component="div"
                    count={100}
                    rowsPerPage={rowsPerPage}
                    page={page}
                    onChangePage={handleChangePage}
                    onChangeRowsPerPage={handleChangeRowsPerPage}
                />
            </TableContainer>
            <ToastContainer />
        </div>

    )
}
