import { Button, makeStyles, Table, TableBody, TableCell, TableHead, TableRow } from '@material-ui/core';
import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import { deleteUser } from '../../enviornment/Apis'
import axios from 'axios'
import * as Apis from '../../enviornment/Apis'
import './designation.css';

const useStyle= makeStyles({
    table: {
        width: '70%',
        margin: '10px 0 0 100px',
        fontSize:'50'   
    },
    thead:{
        '& > *':{
            background: '#000000',
            color: '#ffffff'
        }
    }
})
export default function Editdesignation() {

    const classes=useStyle();
    const [users, setUsers] = useState([]);
    useEffect(
        ()=>{
            getData()  
        },[]
    )
    const getData = () => {
        let data = {
            createdById: "61a0dab0777b848f7b22f811",
        }
        axios.post(Apis.deslist(), data).then((response) => {
            setUsers(response.data.data)
        })

    };

    const deleteUserData= async (id)=>{
          await deleteUser(id);
          getData();
    }

    return (
        <div>
            <Link to="/adddesignation" className="btn btn-primary marginleft">Add New Designation</Link>
            <Table className={classes.table}>
                    <TableHead>
                        <TableRow className={classes.thead}>
                            <TableCell>Designation</TableCell>
                            <TableCell>Action</TableCell>

                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {
                            users.map((user)=>(
                                <TableRow key={user.id}>
                                    <TableCell>
                                        {user.title}
                                    </TableCell>

                                    <TableCell>
                                        <Button variant="contained" color="primary" style={{marginRight:10}} component={Link} to={`/editdesignation/${user.id}`}>Edit</Button>
                                        <Button variant="contained" color="secondary" onClick={()=>deleteUserData(user.id)} >Delete</Button>
                                    </TableCell>
                                </TableRow>
                            ))
                        }
                    </TableBody>
            </Table>
        </div>
    )
}
