import { Button, makeStyles, Table, TableBody, TableCell, TableHead, TableRow } from '@material-ui/core';
import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios'
import * as Apis from '../../enviornment/Apis'
import './designation.css';
import TablePagination from '@mui/material/TablePagination';

const useStyle = makeStyles({
    table: {
        width: '70%',
        margin: '10px 0 0 100px',
        fontSize: '50'
    },
    thead: {
        '& > *': {
            background: '#000000',
            color: '#ffffff'
        }
    }
})
export default function Designationlist() {

    const classes = useStyle();
    const [users, setUsers] = useState([]);
    //Pagination starts
    const [page, setPage] = React.useState(1);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);
    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(parseInt(event.target.value, 10));
        setPage(0);
    };
    // pagination ends
    useEffect(
        () => {
            getData()

        }, []
    )
    const getData = () => {
        let data = {
            createdById: "61a0dab0777b848f7b22f811",
        }
        axios.post(Apis.deslist(), data).then((response) => {
            setUsers(response.data.data)
        })

    };

    const deleteUserData = (_id) => {
        axios.post(Apis.delDes(), {
            _id: _id
        }).then((response) => {
            getData()
            toast(response.data.message);
        })

    }

    return (
        <div>
            <Link to="/adddesignation" className="btn btn-primary marginleft">Add New Designation</Link>
            <Table className={classes.table}>
                <TableHead>
                    <TableRow className={classes.thead}>

                        <TableCell>S.No</TableCell>
                        <TableCell></TableCell>
                        <TableCell>Designation</TableCell>
                        <TableCell></TableCell>
                        <TableCell>Action</TableCell>

                    </TableRow>
                </TableHead>
                <TableBody>
                    {
                        users.map((user, index) => (
                            <TableRow key={index}>

                                <TableCell>
                                    {index + 1}
                                </TableCell>
                                <TableCell>
                                </TableCell>

                                <TableCell>
                                    {user.title}
                                </TableCell>
                                <TableCell>

                                </TableCell>
                                <TableCell>
                                    <Button variant="contained" color="primary" style={{ marginRight: 10 }} component={Link} to={`/editdesignation/${user._id}`}>Edit</Button>
                                    <Button variant="contained" color="secondary" onClick={() => { deleteUserData(user._id) }} >Delete</Button>
                                </TableCell>
                            </TableRow>
                        ))
                    }
                </TableBody>
            </Table>
            <TablePagination
                component="div"
                count={100}
                page={page}
                onPageChange={handleChangePage}
                rowsPerPage={rowsPerPage}
                onRowsPerPageChange={handleChangeRowsPerPage}
            />
            <ToastContainer />
        </div>

    )
}
