import { Button, makeStyles, Table, TableBody, TableCell, TableHead, TableRow } from '@material-ui/core';
import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import { getUsers, deleteUser } from '../../Apis/Apis'



const useStyle= makeStyles({
    table: {
        width: '90%',
        margin: '10px 0 0 0',
        fontSize:'50'   
    },
    thead:{
        '& > *':{
            background: '#000000',
            color: '#ffffff'
        }
    }
})
export default function Userlist() {

    const classes=useStyle();
    const [users, setUsers] = useState([]);
    useEffect(
        ()=>{
            getData()  
        },[]
    )
    const getData= async()=>{
        const response= await getUsers();
        console.log(response.data);
        setUsers(response.data)
    };

    const deleteUserData= async (id)=>{
          await deleteUser(id);
          getData();
    }

    return (
        <div>
            <Link to="/adduser" className="btn btn-primary mt-3">Add New User</Link>
            <Table className={classes.table}>
                    <TableHead>
                        <TableRow className={classes.thead}>
                            <TableCell>Id</TableCell>
                            <TableCell>Name</TableCell>
                            <TableCell>Email</TableCell>
                            <TableCell>Contact Number</TableCell>
                            <TableCell>Action</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {
                            users.map(user=>(
                                <TableRow key={user.id}>
                                    <TableCell>
                                        {user.id}
                                    </TableCell>
                                    <TableCell>
                                        {user.name}
                                    </TableCell>
                                    <TableCell>
                                        {user.email}
                                    </TableCell>
                                    <TableCell>
                                        {user.mobile}
                                    </TableCell>
                                    <TableCell>
                                        <Button variant="contained" color="primary" style={{marginRight:10}} component={Link} to={`/edituser/${user.id}`}>Edit</Button>
                                        <Button variant="contained" color="secondary" onClick={()=>deleteUserData(user.id)} >Delete</Button>
                                    </TableCell>
                                </TableRow>
                            ))
                        }
                    </TableBody>
            </Table>
        </div>
    )
}
