import { Link } from 'react-router-dom'
import axios from 'axios'
import * as Apis from '../../context/Api'
import React, { useState, useEffect } from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Pagination from '@mui/material/Pagination';
import Spinner from '../../Components/spinner/Spinner';
// delete mui button
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import Button from '@mui/material/Button';
import Info from '../../context/Info';
// delete mui button close

export default function Statelist() {
    const [spinner, setspinner] = useState(true)
    const [totalCount, settotalCount] = useState(0)
    const [stateList, setstateList] = useState([]);
    const [sortOrder, setsortOrder] = useState("asc")
    const [deletId, setdeletId] = useState('')
    const [open, setOpen] = useState(false);
    const [pageNbr, setpageNbr] = useState()






    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };


    let Count = 5

    const sorting = (col) => {
        if (sortOrder === "asc") {
            const sorted = [...stateList].sort((a, b) =>
                a[col].toLowerCase() > b[col].toLowerCase() ? 1 : -1
            );
            setstateList(sorted);
            setsortOrder("dsc")
        }
        if (sortOrder === "dsc") {
            const sorted = [...stateList].sort((a, b) =>
                a[col].toLowerCase() < b[col].toLowerCase() ? 1 : -1
            );
            setstateList(sorted);
            setsortOrder("asc")
        }
    }

    useEffect(() => {
        getData(1)
    }
        , [])

        const getData = (page) => {
            let data = {
                createdById: Info.userInfo._id,


                page: page,
                count: Count,



            }

            axios.post(Apis.stateList(), data).then((response) => {
                setstateList(response.data.data)
                settotalCount(response.data.totalCount)
                setspinner(false)
                setpageNbr(page)


            })

        };

    const deleteTitle = (_id) => {
        let dele = {
            _id: _id,
            userId: Info.userInfo._id
        }
        axios.post(Apis.StateDelete(), dele, { headers: { 'x-access-token': Info.token } }).then((response) => {
            toast(response.data.message);
            getData()
            setOpen(false);
        })

    }

    return (
        <div>
            {
                spinner && <Spinner />
            }
            <div className="container">
                <div>
                    <h3 className="text-center">  State list</h3>
                    <div className="text-left d-flex justify-content-end">
                        <Link className="btn btn-warning " to="/Addstate">
                            <h4> State Add </h4>
                        </Link>
                    </div>
                </div>

                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col" >S.no</th>
                            <th scope="col" onClick={() => sorting("title")}>State<i className="bi bi-chevron-down"></i></th>

                            <th scope="col" >Actions</th>

                        </tr>
                    </thead>
                    <tbody>
                        {
                            stateList.map((item, index) => (

                                <tr key={item._id}>
                                    <th scope="row">{Count * (pageNbr - 1) + index + 1}</th>
                                    <td className="col-2">{item.title} </td>


                                    <td >
                                        <Link className="btn btn-success m-2" to={`/StateEdit/${item._id}`}  ><i className="bi bi-pencil-square"></i></Link>

                                        <button className="btn btn-danger" onClick={() => {
                                            setdeletId(item._id)
                                            handleClickOpen()
                                        }}>
                                            <i className="bi bi-trash"></i>
                                        </button>
                                        <Dialog
                                            open={open}
                                            onClose={handleClose}
                                            aria-labelledby="draggable-dialog-title"
                                        >

                                            <DialogContent>
                                                <DialogContentText>
                                                    Are you sure to delete this information..?
                                                </DialogContentText>
                                            </DialogContent>
                                            <DialogActions>
                                                <Button autoFocus onClick={handleClose}>
                                                    No
                                                </Button>
                                                <Button onClick={() => { deleteTitle(deletId) }}>Yes </Button>

                                            </DialogActions>
                                        </Dialog>
                                    </td>
                                </tr>
                            )
                            )}

                    </tbody>
                </table>
                <div>
                    {totalCount > Count ? (
                        <div className="d-flex justify-content-center m-4">

                            <Pagination
                                count={Math.ceil(totalCount / Count)}
                                showFirstButton
                                showLastButton
                                onChange={(event, value) => {
                                    getData(value)
                                }}
                            />
                        </div>
                    ) : null}
                </div>
            </div>
            <ToastContainer />
        </div>
    )
}
