import { Button, FormControl, FormGroup, Input, InputLabel, makeStyles, Typography } from '@material-ui/core'
import React, { useState, useEffect } from 'react'

import { useFormik } from 'formik'
import { TextField } from '@material-ui/core'
import * as yup from 'yup'
import { useHistory } from 'react-router-dom'
import axios from 'axios'
import * as Apis from '../../enviornment/Apis'
import MenuItem from '@mui/material/MenuItem';

import Select from '@mui/material/Select';


export default function Adddesignation() {


    const [dep, setdep] = useState([]);
    useEffect(
        () => {
            getData()
        }, []
    )
    const getData = () => {
        let data = {
            createdById: "61a0dab0777b848f7b22f811",
        }
        axios.post(Apis.deplist(), data).then((response) => {
            setdep(response.data.data)
        })

    };




    const [age, setAge] = React.useState('');
    const handleChange = (event) => {
        setAge(event.target.value);
    };

    const useStyle = makeStyles(
        {
            container: {
                width: '50%',
                margin: '5% 0 0 25%',
                '& > *': {
                    marginTop: 20
                }
            }
        }
    )

    const formik = useFormik({
        initialValues: {
            title: "",
            createdById: "61a0dab0777b848f7b22f811",
            department:'',
            departmentId:"61a4aa19c2051e804c88a99d"


        },

        validationSchema: yup.object({
            title: yup.string().required("Title is required")
        }),

        onSubmit: (values) => {
            console.log(values);

            submitData(values)
        }
    });

    let submitData = (values) => {

        axios.post(Apis.adddes(), values).then((response) => {
            console.log(response,values)

            if (response.data.code === 200) {

                    history.push('/designationlist')
            }
        })

    }



    const history = useHistory();
    const classes = useStyle();

    return (
        <div className={classes.container}>
            <h1>Designation</h1>
            <form onSubmit={formik.handleSubmit}>
                <div className="position-relative">
                    <TextField
                        label="Enter Designation"
                        name="title"
                        onChange={formik.handleChange}
                        onBlur={formik.handleBlur}
                        value={formik.values.title}
                        type='text'
                    /><br></br>
                    {formik.touched.title && formik.errors.title ? (
                        <span className="text_error_message text-danger"> {formik.errors.title} </span>
                    ) : null}
                </div><br />
                <InputLabel id="demo-simple-select-label">description</InputLabel>
                <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    name='department'
                    onChange={formik.handleChange}
                    onBlur={formik.handleBlur}
                    value={formik.values.department}
                >
                    {
                        dep.map((item, index) =>
                            <MenuItem key={index} value={item._id}>{item.title}</MenuItem>
                        )
                    }
                </Select>

                <div><br />
                    <button type="submit" className="btn btn-danger">Add Designation</button>

                </div>

            </form>
        </div>
    )
}
